warp:
shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.03);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (mix (texture (sampler_main, uv).xyz, texture (sampler_main, (
   (0.5 - uv)
  + 0.5)).xyz, vec3(0.5, 0.5, 0.5)) * 2.0);
 vec3 tmpvar_2;
 tmpvar_2 = (1.0 - ((ret_1 * 
   (1.0 - ret_1)
 ) * 4.0));
 vec3 tmpvar_3;
 tmpvar_3.xy = (tmpvar_2.xy * vec2(0.5, 0.5));
 tmpvar_3.z = tmpvar_2.z;
 vec3 tmpvar_4;
 tmpvar_4 = pow (tmpvar_3, (1.2 - tmpvar_3));
 ret_1 = tmpvar_4;
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = tmpvar_4;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.03;
}

comp_hlsl:
shader_body
{

    float2 uv_echo = (uv - 0.5)*1.000*float2(-1,-1) + 0.5;
    ret = lerp( tex2D(sampler_main, uv).xyz, 
                tex2D(sampler_main, uv_echo).xyz, 
                0.50 
              ); //video echo
    ret *= 2.00; //gamma
    float3 retish = 1 - ret*(1-ret)*4; //solarize
    ret = float3(retish[0]*0.5,retish[1]*0.5,retish[2]);
    ret = pow(ret,1.2-ret);
}
