shapes.frame_eqs_eel:
rad=rad+.1*sin(time);
mbass=max(bass_att,3);
xmovn = 0.1*int(rand(10))*mbass*0.015*(1-2*above(int(rand(10)),5));
ymovn = pow(pow(mbass*0.015,2)-pow(xmovn,2),1/2)*(1-2*above(int(rand(10)),5));
//ymovn=.05;
xmov = if(beat,xmovn,xmov*.9);
ymov = if(beat,ymovn,ymov*.9);
q1=oldx;
q2=oldy;
out_x = bor(above(q1+xmov,.9),below(q1+xmov,.1));
out_y = bor(above(q2+ymov,.9),below(q2+ymov,.1));
xmov = xmov + (-2*xmov*out_x);
ymov = ymov + (-2*ymov*out_y);
x =  q1+xmov ;
y = q2+ymov;
q1=x;
q2=y;
oldx = q1;
oldy = q2;

decay = decay - 0.91*(treble);

//Thanks to krash for beat detection (modified)
volume = 0.3*bass+mid;
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01) + (1-below(volume,0.01))*beatrate);
lastbeat = lastbeat + equal(lastbeat,0)*time;
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
runmeanbass =(runmeanbass*2 + bass_att)/3;
peakbass_att = max(bass_att,peakbass_att);
beat = above(volume,0.8)*above(bass_att,runmeanbass*1.1)*below(peakbass_att - bass_att, 0.05*peakbass_att)*above(time - lastbeat, 0.1 + 0.5*(beatrate - 0.1));
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = beat*bass_att + (1-beat)*peakbass_att*(above(time - lastbeat, 2*beatrate)*0.95 + (1-above(time - lastbeat, 2*beatrate))*0.995);
lastbeat = beat*time + (1-beat)*lastbeat;
peakbass_att = max(peakbass_att,1.1*meanbass_att);

waves.point_eqs_eel:
x = 0.5 + sin(sample*103.41 + time*0.1);
y = 0.5 + sin(sample*94.23) + 0.02*sin(time*4.5);
b=abs(sin(sample*100+time));
r=abs(sin(sample*512+time));
g=abs(sin(sample*10+time));

frame_eqs_eel:
wave_r = wave_r + 0.25*sin(1.4*time) + 0.25*sin(2.25*time);
wave_g = wave_g + 0.25*sin(1.7*time) + 0.25*sin(2.11*time);
wave_b = wave_b + 0.25*sin(1.84*time) + 0.25*sin(2.3*time);
warp = 0;

pixel_eqs_eel:
thresh = above(bass_att,thresh)*2+(1-above(bass_att,thresh))*((thresh-1.3)*0.96+1.3);
dx_r = equal(thresh,2)*0.015*sin(5*time)+(1-equal(thresh,2))*dx_r;
dy_r = equal(thresh,2)*0.015*sin(6*time)+(1-equal(thresh,2))*dy_r;

zoom = zoom - (0.5-y*rad*0.2*abs(cos(rad*bass-0.7*sin(ang*12))))*0.1;
dy = dy + dy_r;
dx = dx + dx_r;
sx = sx - dx_r;
sy = sy + (0.4*rad)-cos(3*treb*sin(ang*12)*0.3-rad)*0.1;
sx = sx + (0.4*rad)-cos(3*treb*sin(ang*12)*0.3-rad)*0.1;

warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz - (tmpvar_2.xyz * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = mix ((texture (sampler_main, uv).xyz * 50.0), (texture (sampler_main, (
   ((uv - 0.5) * vec2(-1.0, 1.0))
  + 0.5)).xyz * 50.0), vec3(0.5, 0.5, 0.5));
 ret_1 = ((ret_1 * (1.0 - ret_1)) * 12.0);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{
    float2 uv_echo = (uv - .5)*1*float2(-1,1) + .5;
    ret = lerp( tex2D(sampler_main, uv).xyz*50, 
                tex2D(sampler_main, uv_echo).xyz*50, 
                .5 
              ); //video echo
    ret *= 1.0; //gamma
    ret = ret*(1-ret)*12; //solarize
    ;
}
