ret-=slow_roam_sin.zyx*roam_cos.ywx*.4;
ret = lerp(saturate(ret),float3(0.3,0.4,0.5),GetBlur2(uv).x*4);
ret = lerp(saturate(ret),1,GetPixel(uv).z);
ret = lerp(saturate(ret),0,GetPixel(uv).x*0.6);
