warp_hlsl:
shader_body
{
    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
    ret.z = tex2D( sampler_main, (uv-0.5)*0.95+0.5 ).z;

    // .x = fastest layer, .z = slowest
    float r = 2; 
    ret += float3(-ret.x,ret.x,0)*0.06*r;
    ret += float3(0,-ret.y,ret.y)*0.05*r;     
    ret += float3(0,0,-ret.z)*0.04*r;     

    // ERROR DIFFUSION DITHER - looks great
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0*float3(3,5,8);

    // darken over time
    ret -= 0.0005;
    //ret *= 0.99;
    //ret *= 0.97 + 0.03*saturate( length(uv - uv_orig)*200 );  // center only
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret = ret*1.5 - 0.1;

    float4 g;
    float3 d = float3(texsize.zw * 4, 0);
    g.x = lum(GetBlur1(uv + d.xz));
    g.y = lum(GetBlur1(uv - d.xz));
    g.z = lum(GetBlur1(uv + d.zy));
    g.w = lum(GetBlur1(uv - d.zy));

    ret *= 0.7;
    ret.x += saturate(g.x-g.y)*5;
    ret.z += saturate(g.y-g.x)*5;
