frame_eqs_eel:
warp = 0;
//zoom = 1;

tic = min(time-tin,.1);
tin = time;

vol = (bass_att + treb_att + mid_att)*.333333;

ra = 1/tic*.25;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);
vav = tic*(vav*(1/tic - ra) + ra*(bass+treb+mid)*.33333);

tt = tt + tic*treb;
mt = mt + tic*mid;
bt = bt + tic*bass;
vt = vt + tic*vav;

q1 = tt*3;// wave morph speeds
q2 = mt*3;
q3 = bt*3;

q4 = vt; // wave colour cycle

rb = if(rb,rb,.2 + rand(1001)*.001*.6);
rc = if(rc,rc,.2 + rand(1001)*.001*.6);
rd = if(rd,rd,.2 + rand(1001)*.001*.6);
re = if(re,re,.2 + rand(1001)*.001*.6);


ti = vt*2;
cx = .3 + .1*sin(ti*re) + .1*cos(ti*rc);
cy = .5 + .2*sin(ti*rb) + .1*cos(ti*rd);


q7 = 6;
q8 = 2;

pixel_eqs_eel:
xnum = q7;
ynum = q8;
pi = 3.141592654;

xseg = int(x*xnum);
yseg = int(y*ynum);

xsw = equal((xseg+equal(yseg%2,0))%2,0);
ysw = equal((yseg+1)%2,0);


ox = x - xseg*(1/xnum);
ox = if(xsw,1/xnum - ox,ox);

oy = y - yseg*(1/ynum);
oy = if(ysw,1/ynum - oy,oy);

dx = above(xseg+yseg,0)*(x-ox);
dy = above(xseg+yseg,0)*(y-oy);

rot = rot*(1-above(xseg+yseg,0));

