frame_eqs_eel:
basstime = basstime + bass*0.03;
basstime2 = basstime + bass*0.03;
q1 = basstime2;

//start in most active range
basstime = if(below(basstime,450),450,basstime);
basstime = if(above(basstime,495),450,basstime);

basstime = basstime + bass_att*0.03;
vol = pow(bass+mid+treb,2);
basssum = vol;

stickybit = time%2;

//avg vol
//buffer 1
volAvg = volAvg + vol*equal(stickybit,1);
//number of samples 1 or framerate
sample1 = sample1 + equal(stickybit,1);
//buffer 2
volAvg2 = volAvg2 + vol*equal(stickybit,0);
//number of samples 2
sample2 = sample2 + equal(stickybit,0);

//transision
edge = bnot(equal(bit2,stickybit));

volAvg = volAvg - volAvg*edge*stickybit;
volAvg2 = volAvg2 - volAvg2*edge*equal(stickybit,0);

sample1 = sample1  - sample1*edge*stickybit;
sample2 = sample2  - sample2*edge*equal(stickybit,0);

//test vol against avg buffer lvl
diff = if(equal(stickybit,1), (basssum/(volAvg2/sample2)) , 0);
diff = if(equal(stickybit,0), (basssum/(volAvg/sample1)), diff);

q3 = diff;

bit2 = time%2;

state = if(above(state,5),0,state);
state =  state + above(diff,3);
q4 = state;

difftime = difftime + diff*0.03;
q2 = difftime;

//fix a strange error
difftime = if(above(difftime,2000),0, difftime);

//another state machine for shape tracking
state = state + equal(diff,3);
state = if(above(state,5), 0, state);

xs = 0;
ys = 0;

//state 0 elipse
xs = xs + (equal(0,state))*atan(bass_time*4)*0.1;
ys = ys + (equal(0,state))*cos(bass_time*4)*0.1;

//state 1 diag
xs = xs + (equal(1,state))*cos(bass_time*4)*0.1;
ys = ys + (equal(1,state))*cos(bass_time*4)*0.1;

//state 2 -circle
xs = xs + (equal(2,state))*cos(bass_time*4)*0.1;
ys = ys + (equal(2,state))*sin(bass_time*4)*0.1;

//state 3 parametric arc
xs = xs + (equal(3,state))*cos(bass_time*5)*0.1;
ys = ys + (equal(3,state))*cos(bass_time*9)*0.1;

//state 4 arc
xs = xs + (equal(4,state))*atan(bass_time*6)*0.1;
ys = ys + (equal(4,state))*cos(bass_time*2)*0.1;

//state 5 infinity
xs = xs + (equal(5,state))*cos(bass_time*2)*0.1;
ys = ys + (equal(5,state))*sin(bass_time*1)*0.1;

//count
xpass = xs + xpass;
ypass = ys + ypass;

//project and publish
q6 = 0.3*sin(xpass*0.5);
q7 = 0.3*sin(ypass*0.5);


//toggle zoom in and out
zoom_in = zoom_in + above(diff,4);
zoom_in = if(above(zoom_in,2),0,zoom_in);
q5 = zoom_in;


tex_saw = tex_saw - 0.002*diff;
tex_saw = if(below(tex_saw,0.1),1,tex_saw);
q8 = tex_saw;
monitor = tex_saw;

