warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_fc_main, uv);
 ret_1 = tmpvar_2.xyz;
 if ((((tmpvar_2.x + tmpvar_2.y) + tmpvar_2.z) < ((
   ((mid + bass) + treb)
  / 3.0) * 0.3))) {
   ret_1 = (tmpvar_2.xyz + vec3(0.6, 0.6, 0.6));
 } else {
   ret_1 = ((ret_1 - 0.005) * 0.99);
 };
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 float tmpvar_1;
 tmpvar_1 = abs(((
   fract((((ang / 6.28) * 7.0) + (time * 0.05)))
  * 2.0) - 1.0));
 vec2 tmpvar_2;
 tmpvar_2.x = cos(tmpvar_1);
 tmpvar_2.y = sin(tmpvar_1);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (-0.3 + (1.7 * texture (sampler_main, (0.5 + 
   ((pow (rad, 0.5) * 0.5) * tmpvar_2)
 )).xyz));
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    //ret = lerp(GetBlur1(uv)*1,ret,ret);
    // darken (decay) over time
    if (ret.x+ret.y+ret.z < (0+((mid+bass+treb)/3)*.3)){
       ret.x = ret.x+.6;
       ret.y = ret.y+.6;
       ret.z = ret.z+.6;
       }else{
       ret = (ret - .005)*0.99; //or try: ret -= 0.004;
    }
}

comp_hlsl:
