comp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 vec4 tmpvar_2;
 tmpvar_2.w = 0.0;
 tmpvar_2.xyz = ret_1;
 vec2 tmpvar_3;
 tmpvar_3.x = (1.0 - uv.x);
 tmpvar_3.y = uv.y;
 ret_1 = (max (tmpvar_2, texture (sampler_main, tmpvar_3)).xyz * 1.5);
 ret_1 = (ret_1 * (0.9 + (0.1 * hue_shader)));
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz;
    //ret = lerp(GetBlur1(uv)*1,ret,ret);
    // darken (decay) over time
    if (ret.x+ret.y+ret.z < (0+((mid+bass+treb)/3)*.3)){
       ret.x = ret.x+.6;
       ret.y = ret.y+.6;
       ret.z = ret.z+.6;
       }else{
       ret = (ret - .005)*0.99; //or try: ret -= 0.004;
    }
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret = max(ret, tex2D(sampler_main, float2(1-uv.x,uv.y)));
    ret *= 1.50; //old gamma effect
    ret *= 0.90 + 0.10*hue_shader; //old hue shader effect
    ret *= ret; //old darken effect    
}
