cthr=.99;
mq21=if(above(chng,cthr),rand(3),mq21);
mq22=if(above(chng,cthr),rand(3),mq22);
mq23=if(above(chng,cthr),rand(3),mq23);
mq24=if(above(chng,cthr),rand(2),mq24);
mq25=if(above(chng,cthr),rand(2),mq25);
mq26=if(above(chng,cthr),rand(2),mq26);
mq27=if(above(chng,cthr),rand(1),mq27);
mq28=if(above(chng,cthr),rand(1),mq28);
mq29=if(above(chng,cthr),rand(1)*.3,mq29);
mq30=if(above(chng,cthr),rand(1)*.3,mq30);
monitor=chng;
q21=mq21;q22=mq22;q23=mq23;q24=mq24;q25=mq25;q26=mq26;
q27=mq27;q28=mq28;q29=mq29;q30=mq30;

monitor=mq1;
basstime = basstime + bass*0.03;
basstime2 = basstime + bass*0.03;
q1 = basstime2;

//start in most active range
basstime = if(below(basstime,1000),1000,basstime);
//basstime = if(above(basstime,495),450,basstime);

basstime = basstime + bass_att*0.03;
vol = pow(bass+mid+treb,2);
basssum = vol;

stickybit = time%2;

//avg vol
//buffer 1
volAvg = volAvg + vol*equal(stickybit,1);
//number of samples 1 or framerate
sample1 = sample1 + equal(stickybit,1);
//buffer 2
volAvg2 = volAvg2 + vol*equal(stickybit,0);
//number of samples 2
sample2 = sample2 + equal(stickybit,0);

//transision
edge = bnot(equal(bit2,stickybit));

volAvg = volAvg - volAvg*edge*stickybit;
volAvg2 = volAvg2 - volAvg2*edge*equal(stickybit,0);

sample1 = sample1  - sample1*edge*stickybit;
sample2 = sample2  - sample2*edge*equal(stickybit,0);

//test vol against avg buffer lvl
diff = if(equal(stickybit,1), (basssum/(volAvg2/sample2)) , 0);
diff = if(equal(stickybit,0), (basssum/(volAvg/sample1)), diff);

q3 = diff;

bit2 = time%2;

difftime = difftime + diff*0.03;
q2 = difftime;

//fix a strange error
difftime = if(above(difftime,2000),0, difftime);

monitor = abs(cos(time))*3.14;

rarr = rand(100)*0.01;

decay_r = 0.5 + 0.2*(abs(sin(diff+rarr)));
decay_g = 0.5 + 0.25*(abs(cos(diff+rarr)));
decay_b = 0.5 + 0.30*(abs(atan(diff+rarr)));


//compute unit vec to scale on
xxx = sin(difftime*0.99);
yyy = sin(difftime*0.99);
zzz = sin(difftime*0.94);

//normalise
norm = 1/sqrt(xxx*xxx + yyy*yyy + zzz*zzz);
xxx = xxx*norm;
yyy = yyy*norm;
zzz = zzz*norm;

monitor = ZZZ;

q5 = xxx;
q6 = yyy;
q7 = zzz;

