frame_eqs_eel:
wave_r = mv_b;
wave_b = mv_g;
wave_g = mv_r;
b=max(bass-1,0);
m=max(mid-1,0);
t=max(treb-1,0);
mv_r=.6*abs(sin(time*2.6+1.23+cos(time*4.87+2.145)))+(b/30);
mv_g=.6*abs(sin(time*4.451+2.89+cos(time*3.74+.78)))+(m/30);
mv_b=.6*abs(sin(time*3.845+1.23+cos(time*2.6+3)))+(t/30);

q1=(3.14*cos(time*1.45 + 1.54 + cos((bass_att*.1)+time+ 3.22)));
q2=(3.14*cos(time*2.4 + 2.69 + cos((bass_att*.1)+time+.65)));
q3=time%50;
f=if(equal(endframe,0),1,0);
beat_avg=if(equal(beat_avg,0),bass,beat_avg);
beat_avg=(beat_avg+bass)/2;

max_bass = if( beat*above(f*bass_att,max_bass),0.99*bass_att, max_bass );
max_bass = if( equal(frame%3000,0),0.8*max_bass,max_bass*.9999);
max_bass=if( above(max_bass,10*beat_avg),10*beat_avg,max_bass);
trig=if( equal(beat,1)*above(bass_att,max_bass),1,0);
endframe=if(equal(trig,1)*equal(endframe,0), frame+4*min(interval,FPS),endframe);
trig=if(equal(frame,endframe),0,trig);
endframe=if(above(frame,endframe),0,endframe);
q4=if(above(endframe,0),1,0);
wave_a=0;
rot=.1*sin(time*1.53+cos(time*4.676+3.43));
rot=rot*(1+.4*q4);
ob_size=.3+.7*abs(cos(time*2.45 + 1.566 +sin(time*5 +2.43)));

ob_r=mv_r*(rand(100)/100);
ob_g=mv_g*(rand(100)/100);
ob_b=mv_b*(rand(100)/100);
ob_a=.05*abs(cos(time*2.45 + 1.566 +sin(time+2)));









mv_x=if(equal(q4,1),54+10*cos(time*10),64);


zoom=if(equal(rand(40+60*(q4)),25),1.6,zoom);
// -------------------------------- Beat Detective 007 ----------------------------------
sure=if(equal(sure,0),.6,sure);
interval=if(equal(interval,0),40,interval);
lastbeat=if(equal(lastbeat,0),frame-FPS,lastbeat);
dbass=(bass-pbass)/FPS;
beat=above(dbass,.6*maxdbass)*above(frame-lastbeat,FPS/3);
sure=if(beat*below(abs(frame-(interval+lastbeat)),FPS/5),
min(.095+sure,1),beat*(sure-.095)+(1-beat)*sure*.9996);
sure=max(.5,sure);
cheat=if(above(frame,lastbeat+interval+ int(FPS/10))*above(sure,.91),1,cheat);
beat=if(cheat,1,beat);
sure=if(cheat,.95*sure,sure);
maxdbass=max(maxdbass*.999,dbass);
maxdbass=max(.012,maxdbass);
maxdbass=min(.02,maxdbass);
interval=if(beat, frame-lastbeat,interval);
lastbeat=if(beat,frame-cheat*int(FPS/10),lastbeat);
cheat=0;
pbass=bass;
// ----------------------------------------------------------------------------------------------

zoom = if(equal(beat,1),zoom+bass*.7,zoom);
monitor=sx;
