warp_hlsl:
shader_body
{

ret = 1.25*GetBlur3(uv)-.25*GetBlur1(uv);

uv *= (1+.15*tan(1.33*GetBlur1(saturate(ret.xy))));
uv -= .075*tan(1.33*GetBlur1(saturate(ret.xy)));


    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

