shapes.frame_eqs_str:
tm=q1;

xp=0;
yp=0.1;
zp=0;

//wrist movement;
ang=sin(tm*2 )*0.5 +0.5;

xq=xp;
sinang=sin(ang);cosang=cos(ang);
yq=yp*sinang + zp*cosang;
zq=yp*cosang - zp*sinang;
