    uv2.x = uv.x*cos(q1) - uv.y*sin(q1);
    uv2.y = uv.x*sin(q1) + uv.y*cos(q1);
    uv2 *= aspect.zw;
    uv2 += 0.5;

    ret = abs( tex2D(sampler_main, uv2).xyz*2.65
                            + GetBlur1(uv2)*-2 );

    ret.xyz *= 1.5;
}
