 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 tmpvar_1.xyz = (texture (sampler_main, uv).xyz * 0.7);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (abs((texture (sampler_main, uv).xyz - 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 )) * 6.0);
 ret_1 = (ret_1 * 1.333);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

