comp_hlsl:
shader_body
{
float2 uv1 = (uv-0.5)*aspect.xy;

//float ang1 = atan2(uv1.y, uv1.x);
//float rad1 = log(length(uv1)-0.1);

float rad2 = length(uv1) + .1;
float rad1 = .1/rad2 ;

float2 uv2 = float2 (ang/3.14, rad1);
uv2.y = uv2.y  +0.2*time;
uv2.x = uv2.x  +.0*time;

float2 uv3 = float2 (ang/3.14, rad1*1.5);
uv3.y = uv3.y  +0.08*time ;
uv3.x = uv3.x  + time/32;

float3 crisp = 1*GetPixel(uv2) + GetPixel(uv3);
crisp += 2*GetBlur2(frac(uv2)) + 2*GetBlur2(frac(uv3));

float3 lay1 = float3 (0,0,1)*uv.y*pow(1-rad,8);

crisp = 3.5*crisp * pow(rad,1);

float mask = saturate(1-200*rad);

ret = crisp + lay1*mask + mask * GetPixel(uv);

}
