warp:
shader_body { 
 vec2 uv_1;
 uv_1 = (uv - 0.5);
 uv_1 = (uv_1 * (1.0 + (
   ((q4 * uv_1.y) + (q5 * uv_1.x))
  * 0.1)));
 uv_1 = (uv_1 + 0.5);
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv_1);
 vec2 tmpvar_3;
 tmpvar_3.x = q5;
 tmpvar_3.y = -(q4);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ((max (tmpvar_2.xyz, texture (sampler_main, 
   (uv_1 + (((
     (tmpvar_2.xy - 0.5)
    * texsize.zw) * 13.0) * tmpvar_3))
 ).xyz) - 0.003) * 0.98);
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = texture (sampler_main, uv).xyz;
 vec4 tmpvar_2;
 tmpvar_2.w = 0.0;
 tmpvar_2.xyz = ret_1;
 vec2 tmpvar_3;
 tmpvar_3.x = (1.0 - uv.x);
 tmpvar_3.y = uv.y;
 ret_1 = (max (tmpvar_2, texture (sampler_main, tmpvar_3)).xyz * 1.5);
 ret_1 = (ret_1 * (0.9 + (0.1 * hue_shader)));
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    uv -= 0.5;
    uv *= 1 + (q4*uv.y + q5*uv.x)*0.1;
    uv += 0.5;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;


    ret = max(ret,  tex2D(sampler_main, uv + (ret.xy-0.5)*texsize.zw*13*float2(q5,-q4)).xyz );   

    // darken (decay) over time
    ret = (ret - 0.003)*0.98;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;
    ret = max(ret, tex2D(sampler_main, float2(1-uv.x,uv.y)));
    ret *= 1.50; //old gamma effect
    ret *= 0.90 + 0.10*hue_shader; //old hue shader effect
    ret *= ret; //old darken effect    
}
