shapes.frame_eqs_eel:
tm=q1;

flip=flip+1;
flip=flip*below(flip,2);

xp=0;
yp=0;
zp=0;

//spin;
ang=tm*20 + sin(tm*76 + time*4)*0.4;
sa=sin(ang);ca=cos(ang);
xr=xp*sa + yp*ca;
yr=xp*ca - yp*sa;
zr=zp;

xp=xr;
yp=yr + 0.05 + (sin(tm)*0.5 + 0.5)*0.2 + 0.05;
zp=zr;

//wrist movement;
ang=sin(tm*2);

xq=xp;
sa=sin(ang);ca=cos(ang);
yq=yp*sa + zp*ca;
zq=yp*ca - zp*sa;


ang=tm*8;
sa=sin(ang);ca=cos(ang);
xp=xq*sa + yq*ca;
yp=xq*ca - yq*sa;
zp=zq;

//forearm movement;
zp=zp-0.3;
ang=3.14 + sin(tm*2 - 0.5)*2.5;
xq=xp;
sa=sin(ang);ca=cos(ang);
yq=yp*sa + zp*ca;
zq=yp*ca - zp*sa;

//upper arm twist
ang=-1.0 + cos(tm*3 + 0.5);
xp=xq*sa + yq*ca;
yp=xq*ca - yq*sa;
zp=zq;

//upper arm outward;
zp=zp-0.35;
ang=cos(tm*1)*1.75 - 1.05;
sa=sin(ang);ca=cos(ang);
xq=xp*sa + zp*ca;
yq=yp;
zq=xp*ca - zp*sa;

//upper arm up down;
ang=cos(tm);
xp=xq;
sa=sin(ang);ca=cos(ang);
yp=yq*ca - zq*sa;
zp=yq*sa + zq*ca;

//xp=xq;yp=yq;zp=zq;


//project into screenspace and draw on screen
zp=zp+1.5;
xs=xp/zp;
ys=yp/zp;

x=xs+0.5;
y=ys*1.3+0.5;


a=0.7;

