comp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2.x = rad;
 tmpvar_2.y = uv.y;
 ret_1 = (texture (sampler_main, tmpvar_2).xyz + ((texture (sampler_blur1, tmpvar_2).xyz * scale1) + bias1));
 ret_1 = (ret_1 * vec3(1.04, 0.64, 0.4));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

