warp:
shader_body { 
 vec2 v_1;
 vec3 ret_2;
 v_1 = ((normalize(
   (uv - 0.5)
 ) * aspect.xy) * (texsize.zw * 3.0));
 ret_2 = (0.25 * ((
   (texture (sampler_main, uv).xyz + texture (sampler_main, (uv + (v_1 * 2.5))).xyz)
  + texture (sampler_main, 
   (uv + (v_1 * 5.5))
 ).xyz) + texture (sampler_main, (uv + 
   (v_1 * -4.0)
 )).xyz));
 ret_2 = (ret_2 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_2;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 3.0));
 ret_1 = (ret_1 + ((texture (sampler_blur2, 
   (((uv - 0.5) * 0.333) + 0.5)
 ).xyz * scale2) + bias2));
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = ret_1;
 ret = tmpvar_2.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame 3 times, creating radial blur
    float2 v = normalize(uv - 0.5)*aspect.xy;
    //v = v.yx * float2(1,-1);
    v *= texsize.zw*3;
    ret = 0.25*(   tex2D( sampler_main, uv ).xyz
                   +  tex2D(sampler_main, uv + v*2.5 ).xyz
                   +  tex2D(sampler_main, uv + v*5.5 ).xyz
                   +  tex2D(sampler_main, uv + v*-4  ).xyz
    );

    // darken over time
    ret -= 0.01;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 +
              GetBlur1(uv)*3 +
              GetBlur2(uv)*0 +
              GetBlur3(uv)*0 +
              0;
    ret += GetBlur2( (uv-0.5)*0.333 + 0.5);
    ret *= 1;
}
