warp_hlsl:
shader_body
{
    // PAINTERLY EFFECT:
    float2 uv2 = uv;
    const float mipbias = 0;
    float2 delta2 = texsize.zw*float2(1,1);
    float3 blurry_color = tex2D( sampler_main, uv2 + delta2 ).xyz;
    uv2.xy += (blurry_color.xy-0.37) * 0.02;

    // sample previous frame
    ret = tex2D( sampler_main, uv2 ).xyz;

    // darken over time
    ret -= 0.004;    
}

comp_hlsl:
shader_body
{

//Code bits by Flexi and Geiss

    float2 d = texsize.zw*6;
    float3 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) );
    float3 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) );
float2 uv_y = uv-0.25*(float2(lum(dx),lum(dy)));

ret = .25*lum(saturate(2*GetBlur3(uv)));
ret -= .8*lum(saturate(20*(0.6*GetBlur2(uv)-.01)-2));
ret = ret + lum(saturate(30*((GetPixel(uv)+GetBlur1(uv)*.15)-.01)-2))-.1;
ret += .55;
ret = lerp(ret,ret*(GetBlur3(uv_y)-GetBlur1(uv_y)),pow(hue_shader,ret));

float3 ret2 = -.5*lum(GetBlur3(uv_y));
ret2 += .8*GetBlur1(uv_y);
ret2 = ret-.9*GetPixel(uv_y)-.1;
ret2 -= .75;
ret2 = lerp(ret2,1.2*ret2*(GetBlur3(uv)-GetBlur1(uv)),pow(hue_shader.zxy,ret));

ret = abs(ret-1.2*ret2);
//ret = ret2;
ret *= ret;

