init_eqs_eel:
// clear screen first
ob_r = 0; ob_g = 0; ob_b = 0; ob_a = 1;
ib_r = 0; ib_g = 0; ib_b = 0; ib_a = 1;
ob_size = 0.5;
ib_size = 0.5;
start = 1;

frame_eqs_eel:
chng=sin(time*.5);
cthr=.9999;
mq21=if(above(chng,cthr),rand(3),mq21);
mq22=if(above(chng,cthr),rand(3),mq22);
mq23=if(above(chng,cthr),rand(3),mq23);
mq24=if(above(chng,cthr),rand(2),mq24);
mq25=if(above(chng,cthr),rand(2),mq25);
mq26=if(above(chng,cthr),rand(2),mq26);
mq27=if(above(chng,cthr),rand(1),mq27);
mq28=if(above(chng,cthr),rand(1),mq28);
mq29=if(above(chng,cthr),rand(1)*.3,mq29);
mq31=if(above(chng,cthr),rand(1)*.3,mq31);
monitor=chng;
q21=mq21;q22=mq22;q23=mq23;q24=mq24;q25=mq25;q26=mq26;
q27=mq27;q28=mq28;q29=mq29;q31=mq31;

monitor=mq1;
vol=bass+treb+mid;
atime=atime+vol;
q11=.4+sin(atime*.006        )*.4;
q12=.4+cos(atime*.00613828348)*.4;
q13=.4+sin(atime*.00598593455)*.4;
monitor=q13;
zoom = 1;
warp = 0;
xx1 = xx1*0.9 + (bass);
xx2 = xx2*0.9 + (treb);
yy1 = yy1*0.94 + (treb+bass)*0.75;
x1 = 0.5 + xx2-xx1;
y1 = 0.5 + yy1;
spring = 10;
grav = .5;
resist = 1;
bounce = 0.5;
dt = .001000000;
vx2 = vx2*(1-resist*dt) + dt*((x1+x3-2*x2)*spring);
vy2 = vy2*(1-resist*dt) + dt*((y1+y3-2*y2)*spring-grav);
vx3 = vx3*(1-resist*dt) + dt*((x2+x4-2*x3)*spring);
vy3 = vy3*(1-resist*dt) + dt*((y2+y4-2*y3)*spring-grav);
vx4 = vx4*(1-resist*dt) + dt*((x3-x4)*spring);
vy4 = vy4*(1-resist*dt) + dt*((y3-y4)*spring-grav);
x2 = x2 + vx2;
y2 = y2 + vy2;
x3 = x3 + vx3;
y3 = y3 + vy3;
x4 = x4 + vx4;
y4 = y4 + vy4;
vx2 = if(above(x2,0),vx2,abs(vx2)*bounce);
vx2 = if(below(x2,1),vx2,-abs(vx2)*bounce);
vx3 = if(above(x3,0),vx3,abs(vx3)*bounce);
vx3 = if(below(x3,1),vx3,-abs(vx3)*bounce);
vx4 = if(above(x4,0),vx4,abs(vx4)*bounce);
vx4 = if(below(x4,1),vx4,-abs(vx4)*bounce);
vy2 = if(above(y2,0),vy2,abs(vy2)*bounce);
vy2 = if(below(y2,1),vy2,-abs(vy2)*bounce);
vy3 = if(above(y3,0),vy3,abs(vy3)*bounce);
vy3 = if(below(y3,1),vy3,-abs(vy3)*bounce);
vy4 = if(above(y4,0),vy4,abs(vy4)*bounce);
vy4 = if(below(y4,1),vy4,-abs(vy4)*bounce);

ww = ww + (vx3-vx4)*10 - (x4-x3)*0.3+ bb*asin(1)*2;

startx = endx;
starty = endy;
endx = startx + cos(ww)*0.004;
endy = starty + sin(ww)*0.004;

monitor = x4;

bb = 1- above(endx,0.2)*above(endy,0.2)*below(endx,0.8)*below(endy,0.8);

endx = min(0.8,max(0.2,endx));
endy = min(0.8,max(0.2,endy));


q9 = startx; q10 = starty; q11 = endx; q12 = endy;
q13 = startx*1.1; q14 = starty*1.1; q15 = endx*1.1; q16 = endy*1.1;
q17 = startx*bass; q18 = starty*treb; q19 = endx*mid; q20 = endy*1.3;
q21 = starty*bass; q22 = startx*treb; q23 = endy*mid; q24 = endx*1.3;


ob_size = start;
ib_size = start;
start = start-1;

warp:
shader_body { 
 vec3 ret_1;
 vec3 tmpvar_2;
 tmpvar_2 = max (texture (sampler_main, uv).xyz, texture (sampler_main, ((
   (uv - 0.5)
  * 
   (1.0 - (3.0 * sqrt(dot (texsize.zw, texsize.zw))))
 ) + 0.5)).xyz);
 ret_1 = tmpvar_2;
 vec4 tmpvar_3;
 tmpvar_3 = texture (sampler_noisevol_hq, (((vec3(0.05, 0.05, 0.0) * uv.xyy) * (
   (q27 * texsize.xyy)
  * texsize_noisevol_hq.zww)) + ((time * vec3(0.0, 0.0, 1.0)) * q29)));
 if (((tmpvar_2.x > (q21 * q13)) && (tmpvar_2.x <= (q24 * q11)))) {
   ret_1.y = (tmpvar_2.y - (tmpvar_3.x * 0.5));
 };
 if (((ret_1.y > (q22 * q11)) && (ret_1.y <= (q25 * q12)))) {
   ret_1.z = (tmpvar_2.z - (tmpvar_3.y * 0.5));
 };
 if (((ret_1.z > (q23 * q12)) && (ret_1.z <= (q26 * q13)))) {
   ret_1.x = (tmpvar_2.x - (tmpvar_3.z * 0.5));
 };
 ret_1 = (max (ret_1, texture (sampler_main, (
   ((uv - 0.5) * (1.0 + (3.0 * sqrt(
     dot (texsize.zw, texsize.zw)
   ))))
  + 0.5)).xyz) - 0.02);
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = ret_1;
 ret = tmpvar_4.xyz;
}

comp:
shader_body { 
 vec2 uv_1;
 vec3 ret_2;
 uv_1 = (uv * 0.5);
 vec3 tmpvar_3;
 tmpvar_3 = max (max (texture (sampler_main, uv_1).xyz, texture (sampler_main, (uv_1 + vec2(0.5, 0.0))).xyz), texture (sampler_main, (uv_1 + vec2(0.0, 0.5))).xyz);
 ret_2 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4 = texture (sampler_noisevol_hq, (((vec3(0.05, 0.05, 0.0) * uv_1.xyy) * (
   (q28 * texsize.xyy)
  * texsize_noisevol_hq.zww)) + ((time * vec3(0.0, 0.0, 1.0)) * q31)));
 if (((tmpvar_3.x > (q26 * q13)) && (tmpvar_3.x <= (q23 * q11)))) {
   ret_2.z = (tmpvar_3.z - (tmpvar_4.x * 0.5));
 };
 if (((tmpvar_3.y > (q25 * q11)) && (tmpvar_3.y <= (q22 * q12)))) {
   ret_2.x = (tmpvar_3.x - (tmpvar_4.y * 0.5));
 };
 if (((ret_2.z > (q24 * q12)) && (ret_2.z <= (q21 * q13)))) {
   ret_2.y = (tmpvar_3.y - (tmpvar_4.z * 0.5));
 };
 ret_2 = (max (ret_2, texture (sampler_main, (uv_1 + vec2(0.5, 0.5))).xyz) * 1.3);
 ret_2 = (0.6 - ret_2);
 vec4 tmpvar_5;
 tmpvar_5.w = 1.0;
 tmpvar_5.xyz = ret_2;
 ret = tmpvar_5.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-3*length(texsize.zw)) + 0.5 ).xyz);
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q27
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q29
                     );
if(ret.x> q21*q13         && ret.x<= q24*q11        )ret.y -= (noise9).x*.5; 
if(ret.y> q22*q11         && ret.y<= q25*q12        )ret.z -= (noise9).y*.5; 
if(ret.z> q23*q12         && ret.z<= q26*q13        )ret.x -= (noise9).z*.5; 
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+3*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.02;
}

comp_hlsl:
shader_body
{
    uv *= 0.5;
    ret = tex2D(sampler_main, uv).xyz;
    ret = max(ret, tex2D(sampler_main,uv + float2(0.5,0)).xyz);
    ret = max(ret, tex2D(sampler_main,uv + float2(0,0.5)).xyz);
float4 noise9 = tex3D(sampler_noisevol_hq, 
                      ((uv.xyy*q28
                       )*texsize.xyy*texsize_noisevol_hq.zww
                      ).xyz*
                      float3(1,1,0)*0.05 + 
                      time*float3(0,0,1)*q31
                     );
if(ret.x> q26*q13         && ret.x<= q23*q11        )ret.z -= (noise9).x*.5; 
if(ret.y> q25*q11         && ret.y<= q22*q12        )ret.x -= (noise9).y*.5; 
if(ret.z> q24*q12         && ret.z<= q21*q13        )ret.y -= (noise9).z*.5; 
    ret = max(ret, tex2D(sampler_main,uv + float2(0.5,0.5)).xyz);
    ret *= 1.3; // a little bit of overbright
ret=.6-ret;
}
