init_eqs_eel:
// clear screen first
ob_r = 0; ob_g = 0; ob_b = 0; ob_a = 1;
ib_r = 0; ib_g = 0; ib_b = 0; ib_a = 1;
ob_size = 0.5;
ib_size = 0.5;
start = 1;

frame_eqs_eel:
zoom = 1;
warp = 0;
xx1 = xx1*0.9 + (bass);
xx2 = xx2*0.9 + (treb);
yy1 = yy1*0.94 + (treb+bass)*0.75;
x1 = 0.5 + xx2-xx1;
y1 = 0.5 + yy1;
spring = 10;
grav = .5;
resist = 1;
bounce = 0.5;
dt = .001000000;
vx2 = vx2*(1-resist*dt) + dt*((x1+x3-2*x2)*spring);
vy2 = vy2*(1-resist*dt) + dt*((y1+y3-2*y2)*spring-grav);
vx3 = vx3*(1-resist*dt) + dt*((x2+x4-2*x3)*spring);
vy3 = vy3*(1-resist*dt) + dt*((y2+y4-2*y3)*spring-grav);
vx4 = vx4*(1-resist*dt) + dt*((x3-x4)*spring);
vy4 = vy4*(1-resist*dt) + dt*((y3-y4)*spring-grav);
x2 = x2 + vx2;
y2 = y2 + vy2;
x3 = x3 + vx3;
y3 = y3 + vy3;
x4 = x4 + vx4;
y4 = y4 + vy4;
vx2 = if(above(x2,0),vx2,abs(vx2)*bounce);
vx2 = if(below(x2,1),vx2,-abs(vx2)*bounce);
vx3 = if(above(x3,0),vx3,abs(vx3)*bounce);
vx3 = if(below(x3,1),vx3,-abs(vx3)*bounce);
vx4 = if(above(x4,0),vx4,abs(vx4)*bounce);
vx4 = if(below(x4,1),vx4,-abs(vx4)*bounce);
vy2 = if(above(y2,0),vy2,abs(vy2)*bounce);
vy2 = if(below(y2,1),vy2,-abs(vy2)*bounce);
vy3 = if(above(y3,0),vy3,abs(vy3)*bounce);
vy3 = if(below(y3,1),vy3,-abs(vy3)*bounce);
vy4 = if(above(y4,0),vy4,abs(vy4)*bounce);
vy4 = if(below(y4,1),vy4,-abs(vy4)*bounce);

ww = ww + (vx3-vx4)*10 - (x4-x3)*0.3+ bb*asin(1)*2;

startx = endx;
starty = endy;
endx = startx + cos(ww)*0.004;
endy = starty + sin(ww)*0.004;

monitor = x4;

bb = 1- above(endx,0.2)*above(endy,0.2)*below(endx,0.8)*below(endy,0.8);

endx = min(0.8,max(0.2,endx));
endy = min(0.8,max(0.2,endy));


q9 = startx; q10 = starty; q11 = endx; q12 = endy;
q13 = startx*1.1; q14 = starty*1.1; q15 = endx*1.1; q16 = endy*1.1;
q17 = startx*bass; q18 = starty*treb; q19 = endx*mid; q20 = endy*1.3;
q21 = starty*bass; q22 = startx*treb; q23 = endy*mid; q24 = endx*1.3;


ob_size = start;
ib_size = start;
start = start-1;

