warp_hlsl:
shader_body
{
float2 uv_m = 1.0 - abs( frac( uv * 0.5 ) * 2.0 - 1.0 ); // Eo.S' mirror code
ret.x = tex2D( sampler_fc_main, uv_m ).z; // first julia fractal iteration
ret.y = lerp(lerp(tex2D( sampler_fc_main, uv_m ).x, ret.x,0.5), 
             GetPixel(uv_orig).y,
             0.75); // 75% of motion blur to the green channel


float2 d = texsize.zw*6;
float2 uv_z = (uv_orig-0.5)*0.998 + 0.5; // a small dose of zoom-in
float3 dx = ( GetBlur1(uv_z + float2(1,0)*d) - GetBlur1(uv_z-float2(1,0)*d) );
float3 dy = ( GetBlur1(uv_z + float2(0,1)*d) - GetBlur1(uv_z-float2(0,1)*d) );
uv_z -=float2(dx.z,dy.z)*texsize.zw*2; //shortening of the nebulous matter
ret.z = max((tex2d(sampler_fc_main,uv_m).x-0.5)*3*saturate(1-length(uv_orig-0.5)*3.2), // feeded by the inner of the red channel
             tex2d(sampler_fc_main,uv_z).z);
ret.z += (tex2d(sampler_fc_main,uv_z).z - GetBlur1(saturate(uv_z)).z)*0.02; // sort of geiss' skin dot code
}

