    float gy2 = GetPixel(uv2)*c.x + GetBlur1(uv2)*c.y + GetBlur2(uv2)*c.z + GetBlur3(uv2)*c.w;

    float3 g = normalize(float3(gx1-gx2, gy1-gy2, 0.14));

