warp_hlsl:
shader_body
{
    float2 scale = float2(1280,1024)*texsize.zw; // 1280x1024 : the resolution i modelled this shader with
    float1 d = 0.005;
    float1 dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).x*scale.x;
    float1 dy = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).x*scale.y;
    float1 dxb = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).x*scale.x;
    float1 dyb = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).x*scale.y;
    float2 my_uv = uv - float2(dx,dy)*0.01 + float2(dxb,dyb)*0.003;



    float2 v = 0.01;
    ret.x = tex2D( sampler_fw_main, my_uv -floor(my_uv)*0).x;


    ret.x += (ret.x - GetBlur3(uv).x)*.1;
    ret.x += 0.004;


//--------------------------------

    my_uv = uv + float2(dy,-dx)*0.05*(1.2-GetBlur3(uv).y);



    v = 0.01;
    ret.z = tex2D( sampler_fw_main, my_uv).z;

    ret.z += (ret.z - GetBlur1(uv).z)*length(my_uv-uv)*180/length(scale);
    ret.z *= 0.8;
    ret.z += 0.004;



//--------------------------------

    d = 0.01;
    my_uv = float2(-dy,dx)*0.045;

    dx = ( GetBlur2(uv + float2(d,0)) - GetBlur2(uv-float2(d,0)) ).y*scale.x;
    dy = ( GetBlur2(uv + float2(0,d)) - GetBlur2(uv-float2(0,d)) ).y*scale.y;
    my_uv += uv - float2(dx,dy)*0.03;



    v = 0.01;
    ret.y =tex2D( sampler_fw_main, my_uv).y;


    ret.y += (ret.y - GetBlur3(my_uv).y)*0.2 + 0.0;
    ret = lerp(ret, tex2D(sampler_main,uv_orig), 0.2); //motionblur

//ret = 0;
}

