warp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1 = (uv_orig * texsize.xy);
 vec4 tmpvar_2;
 tmpvar_2.w = 1.0;
 tmpvar_2.xyz = (texture (sampler_main, mix (uv, (
   ((uv_orig - 0.5) * 0.91)
  + 0.5), vec2(clamp (
   (2.0 * fract(((
     (tmpvar_1.x + tmpvar_1.y)
