 vec3 ret_1;
 ret_1 = (texture (sampler_main, uv).xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1));
 vec2 tmpvar_2;
 tmpvar_2 = ((0.5 - uv) + 0.5);
 ret_1 = (ret_1 * 4.5);
 ret_1 = (mix (ret_1, (
   (texture (sampler_main, tmpvar_2).xyz - ((texture (sampler_blur1, tmpvar_2).xyz * scale1) + bias1))
  * 4.5), vec3(0.5, 0.5, 0.5)) * ((hue_shader * 8.0) - 7.25));
 ret_1 = (ret_1 * 1.5);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // custom motion for 50% of the pixels...:
    float2 uv2 = (uv_orig - 0.5)*0.91 + 0.5;

    // make every other pixel use 'uv2':
    float2 pix = uv_orig * texsize.xy;
    float even_odd = saturate(2*frac((pix.x + pix.y)*0.5 + 0.25));
    uv = lerp(uv, uv2, even_odd);

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // darken (decay) over time
    //ret = (ret - 0.0015)*0.988;
    ret *= 0.8 + 0.17*saturate(rad*5);
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz*1 - GetBlur1(uv);
    float2 uv_echo = (uv - 0.5)*float2(-1,-1) + 0.5;
    float3 ret2 = tex2D(sampler_main, uv_echo).xyz - GetBlur1(uv_echo);
    ret *= 4.5;
    ret2 *=4.5;
    ret = lerp(ret,ret2,0.50);
    ret *= (hue_shader*8)-7.25;
    ret *= 1.5;
}
