shapes.frame_eqs_eel:
rad=rad+.1*sin(time);
mbass=max(bass_att,3);
xmovn = 0.1*rand(10)*mbass*0.015*(1-2*above(rand(10),5));
ymovn = pow(pow(mbass*0.015,2)-pow(xmovn,2),1/2)*(1-2*above(rand(10),5));
//ymovn=.05;
xmov = if(beat,xmovn,xmov*.9);
ymov = if(beat,ymovn,ymov*.9);
q1=oldx;
q2=oldy;
out_x = bor(above(q1+xmov,.9),below(q1+xmov,.1));
out_y = bor(above(q2+ymov,.9),below(q2+ymov,.1));
xmov = xmov + (-2*xmov*out_x);
ymov = ymov + (-2*ymov*out_y);
x =  q1+xmov ;
y = q2+ymov;
q1=x;
q2=y;
oldx = q1;
oldy = q2;

decay = decay - 0.91*(treble);

//Thanks to krash for beat detection (modified)
volume = 0.3*bass+mid;
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01) + (1-below(volume,0.01))*beatrate);
lastbeat = lastbeat + equal(lastbeat,0)*time;
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
runmeanbass =(runmeanbass*2 + bass_att)/3;
peakbass_att = max(bass_att,peakbass_att);
beat = above(volume,0.8)*above(bass_att,runmeanbass*1.1)*below(peakbass_att - bass_att, 0.05*peakbass_att)*above(time - lastbeat, 0.1 + 0.5*(beatrate - 0.1));
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = beat*bass_att + (1-beat)*peakbass_att*(above(time - lastbeat, 2*beatrate)*0.95 + (1-above(time - lastbeat, 2*beatrate))*0.995);
lastbeat = beat*time + (1-beat)*lastbeat;
peakbass_att = max(peakbass_att,1.1*meanbass_att);

waves.point_eqs_eel:
x = 0.5 + sin(sample*103.41 + time*0.1);
y = 0.5 + sin(sample*94.23) + 0.02*sin(time*4.5);
b=abs(sin(sample*100+time));
r=abs(sin(sample*512+time));
g=abs(sin(sample*10+time));

frame_eqs_eel:
dec_med = pow (.4, 30/fps);
dec_slow = pow (0.95, 30/fps);
//dec_xlow = pow (0.999, 30/fps);
beat = max (max (bass, mid), treb); 
avg = avg*dec_slow + beat*(1-dec_slow);
is_beat = above(beat, -.2+avg+peak) * above (time, t0+.1);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_med;
index = (index + is_beat) %32;
index2 = (index2 + is_beat*bnot(index))%4;
index3 = (index2 +index3 + is_beat*bnot(index))%4;

is_beat2 = above (time, t1+.1);
t1 = is_beat*time + (1-is_beat)*t1;
q22 = peak;
q24 = is_beat;
q26 = bass_att + mid_att + treb_att;
vol = vol*dec_med + (1-dec_med)*q26;
q27 = index + 1.5;
q28 = index2 +1;

p2 = .3*sin(time) + .3*cos(time/3);
//*****tunnel tilting
q1 = cos(p2 * 3.1416/4);
q2 = sin(p2 * 3.1416/4);
q3 = -q2;
q4 = q1;

//****tunnel viewpoint x,y
vtrig = is_beat * bnot(index%2);
vx = vx * bnot(vtrig) + vtrig * (rand(100)/100-.5);
vy = vy * bnot(vtrig) + vtrig * (rand(100)/100-.5);
vz = vz * bnot(vtrig) + vtrig * (rand(100)/100-.5);

vx = vx* below(index%32,24); //###
vy = vy* above(index%32,16);
q5 = vx*5;
q6 = vy*5;

//***tunnel width
q21 = (1+sin(time))/4+.05;

//**floor proximity
prox = prox - above(vy,.3)*prox*2;
q32 = .5 + .2*prox;
monitor = prox;

//****tunnel rotation
rota = rota +.02*below(index%16,8)/fps*30;
rota1 = rota1*dec_med + (1-dec_med)*rota;
q7 = rota1;

//****tunnel speed
movez = movez + .8/(1+abs(vx)+abs(vy))/fps;
q8 = movez * (1+q21/400);
//dyn. Aenderung von q21 bewirkt auch Richt.wechsel

q15 = -1+8*above(index,26);
//***********************
vxs = vxs * dec_med + (1-dec_med)*vx*20;
vys = vys * dec_med + (1-dec_med)*vy*20;
vzs = vzs * dec_med + (1-dec_med)*vz*20;

z0 = 20+vzs;

p1z = z0;
q9 = vxs/(p1z); 
q10 = vys/(p1z); 
q11 = p1z;

q12 = below (index,24);
q13 = above (index,16);


q14 = 1; // x
q16 = 0; // y

q17 = -1;
q18 = 0;

q20 = q9;
q19 = q4*5;

