warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, uv ).xyz*0;
float3 C = GetPixel(uv_orig)*0;
float2 d = texsize.zw*1;
float3 N = GetPixel(uv_orig+float2(0,1)*d);
float3 E = GetPixel(uv_orig+float2(1,0)*d);
float3 S = GetPixel(uv_orig+float2(0,-1)*d);
float3 W = GetPixel(uv_orig+float2(-1,0)*d);

float3 max = max( C, max( max(N,E), max(S,W) ) );

ret = saturate(max*1) - 0.028;

}

comp_hlsl:
shader_body
{
float2 uv1 = (uv-0.5)*aspect.xy;

float rad2 = length(uv1) + .1;
float rad1 = .1/rad2 ;

float2 uv2 = float2 (ang/3.14, q27*rad1);
uv2.y = uv2.y  +.1*q30;
uv2.x = uv2.x  +.2*time;

float2 uv3 = float2 (ang/3.14, 4*rad1);
uv3.y = uv3.y  -.2*q30;
uv3.x = uv3.x  +.0*time;
uv3 = frac(uv3);

float3 crisp = 2*GetPixel(uv2) + GetPixel(uv3);
crisp = max(crisp,2*GetBlur2(frac(uv2)) + GetBlur2(frac(uv3)));

float3 lay1 = uv.y*pow(1-rad,8)*roam_cos;

crisp = 3*crisp * pow(rad,1);
float mask = saturate(1-4*rad);

ret = crisp + lay1*mask + mask * 2*GetBlur1(frac(uv));

}
