warp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = (uv - 0.5);
 ret_1.x = texture (sampler_main, (tmpvar_2 + 0.5)).x;
 ret_1.y = texture (sampler_main, ((tmpvar_2 * 0.98) + 0.5)).y;
 ret_1.z = texture (sampler_main, ((tmpvar_2 * 0.96) + 0.5)).z;
 ret_1 = (ret_1 - (ret_1 * 0.5));
 ret_1 = ((ret_1 * abs(
   (((texture (sampler_blur2, uv).xyz * scale2) + bias2) - 0.5)
 )) * 5.0);
 ret_1 = (ret_1 - 0.01);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz * (fract(
   (tmpvar_2.xyz * 4.0)
 ) * 2.0));
 ret_1 = (ret_1 + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.1));
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    float3 ret2 = tex2D( sampler_fc_main, uv ).xyz;

    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
    ret.z = tex2D( sampler_main, (uv-0.5)*0.96+0.5 ).z;   

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{
    ret = tex2D(sampler_main, uv).xyz;

    ret *= frac(ret*4)*2;
    ret += GetBlur1(uv) * .1;
    ret *= ret; 
}
