warp_hlsl:
shader_body
{ret=GetPixel(uv);
float d=lum(abs(uv-uv_orig))*0.6+0.003;
float3 one,two,three,four,five;
float3 sex,drugs,rock,roll,bugs;
float3 maxx;
one=max(
abs(ret-GetPixel(float2(uv.x-d,uv.y-d))),
abs(ret-GetPixel(float2(uv.x-d,uv.y)))
);
two=max(
abs(ret-GetPixel(float2(uv.x-d,uv.y+d))),
abs(ret-GetPixel(float2(uv.x,uv.y-d)))
);
three=max(
abs(ret-GetPixel(float2(uv.x,uv.y))),
abs(ret-GetPixel(float2(uv.x,uv.y+d)))
);
four=max(
abs(ret-GetPixel(float2(uv.x+d,uv.y-d))),
abs(ret-GetPixel(float2(uv.x+d,uv.y)))
);
five=abs(ret-GetPixel(float2(uv.x+d,uv.y+d)));
sex=abs(one-ret);
drugs=abs(two-ret);
rock=abs(three-ret);
roll=abs(four-ret);
bugs=abs(five-ret);
maxx=max(one,max(two,max(three,max(four,five))));
maxx=saturate(maxx);
ret=maxx;
ret=(ret+GetPixel(uv+ret.xy*0.01)*0.8)/1.4;
}

