warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1.yz = (tmpvar_2.xyz + ((
   (tmpvar_2.xyz - ((texture (sampler_blur1, uv).xyz * scale1) + bias1))
  * 0.1) - 0.015)).yz;
 ret_1.x = (texture (sampler_main, (uv_orig + (
   ((texture (sampler_noise_lq, ((
     (uv_orig * texsize.xy)
    * 
     (texsize_noise_lq.zw * 0.6)
   ) + rand_frame.xy)).xyz - 0.5).xy * texsize.zw)
  * 4.0))).xyz - 0.01).x;
 ret_1.z = (ret_1.z * 0.96);
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = ret_1;
 ret = tmpvar_3.xyz;
}

comp:
