 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz * (fract(
   (tmpvar_2.xyz * 4.0)
 ) * 2.0));
 ret_1 = (ret_1 + ((
   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.1));
 ret_1 = (ret_1 * ret_1);
 vec4 tmpvar_3;
