shader_body
{
float2 pixelsize = texsize.zw;
float2 uvm = uv;

float2 noise_uv = uv_orig*texsize.xy*texsize_noise_lq.zw + rand_frame.xy;
float4 noiseVal = tex2D(sampler_noise_lq, noise_uv);

ret.xyz = saturate(tex2D( sampler_pw_main,uvm).xyz*float3(1,1,1) - float3(.0,.0,.0));
ret.xyz = tex2D( sampler_pw_main,uvm).xyz;


float3 rsamp = tex2D(sampler_pw_main,uvm + (noiseVal.xy-.5)*12*pixelsize).xyz;
if (length(ret.xy - uvm) > length(rsamp.xy - uvm)) {
ret.xy = rsamp.xy;
}

