warp:
shader_body { 
 vec2 tmpvar_1;
 tmpvar_1 = (uv - 0.5);
 vec4 tmpvar_2;
 tmpvar_2 = sin(((
   (tmpvar_1.yxyx * ((tmpvar_1 * sqrt(
     dot (tmpvar_1, tmpvar_1)
   )) * 12.0).x)
  * vec4(9.0, 9.0, 18.0, 18.0)) + (vec4(0.3, -0.3, -0.3, 0.3) * q2)));
 vec4 tmpvar_3;
 tmpvar_3.w = 1.0;
 tmpvar_3.xyz = (texture (sampler_fc_main, (uv + (
   (tmpvar_2.xy * tmpvar_2.zw)
  * 
   (0.005 * q1)
 ))).xyz - 0.00390625);
 ret = tmpvar_3.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 float tmpvar_4;
 tmpvar_4 = sqrt(dot (tmpvar_3, tmpvar_3));
 vec3 tmpvar_5;
 tmpvar_5 = clamp ((texture (sampler_main, (
   ((tmpvar_3 / tmpvar_4) * 0.1)
  + 0.5)).xyz - (tmpvar_4 * 2.5)), 0.0, 1.0);
 float tmpvar_6;
 tmpvar_6 = clamp ((tmpvar_4 - 0.1), 0.0, 1.0);
 ret_1 = (mix (tmpvar_2.xyz, tmpvar_2.zyx, vec3((tmpvar_4 * 2.0))) + (tmpvar_5 * q1));
 ret_1 = (max (ret_1, (
   clamp ((tmpvar_5 - (tmpvar_6 * 1.5)), 0.0, 1.0)
  * 
   clamp ((tmpvar_6 * 100.0), 0.0, 1.0)
 )) * (1.0 + (
   pow (clamp ((1.0 - (tmpvar_4 * 2.0)), 0.0, 1.0), 16.0)
  * 4.0)));
 ret_1 = (ret_1 * normalize(ret_1));
 ret_1 = (1.0 - exp((ret_1 * -3.0)));
 vec4 tmpvar_7;
 tmpvar_7.w = 1.0;
 tmpvar_7.xyz = ret_1;
 ret = tmpvar_7.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    float2 uv2 = uv;//(uv-0.5)*0.99 + 0.5;

float2 uv4sin = (uv - 0.5);
float freq = uv4sin *  length( uv4sin ) * 12.0 ;
float4 offset = sin( uv4sin.yxyx * freq * float2(9.0,18).xxyy + q2 * float4( 1.0, -1.0, -1.0, 1.0 ) * 0.3  ) ;
uv2 += ( offset.xy * offset.zw ) * 0.005 * q1;


    ret = tex2D( sampler_fc_main, uv2 ).xyz;

float L = 1.0 - length( uv * 2.0 - 1.0 );
L = saturate( L );
    // darken (decay) over time
    ret -= 1.0/256.0;// * L; //or try: ret -= 0.004;
}

comp_hlsl:
shader_body
{
ret = tex2D(sampler_main, uv).xyz;

float2 uv2 = uv - 0.5;
float L = length( uv2 );
float2 uvN = uv2/L;
uv2 = uvN*0.1 + 0.5;
float2 uv3 = uvN * 0.2 + 0.5;

float3 streak = tex2D(sampler_main, uv2).xyz;
streak = saturate( streak -  L * 2.5 ); 

float3 streak2 = tex2D(sampler_main, uv3).xyz;
float streak2Area = saturate( L - 0.1 );
streak2 = saturate( streak - streak2Area * 1.5 ) * saturate( streak2Area * 100.0 );
//streak2 *= treb;


ret = lerp( ret, ret.bgr, L*2.0 );

ret += streak * q1;
ret = max(ret,streak2);



ret *= 1.0 + pow( saturate( 1.0 - L * 2.0 ), 16.0 ) * 4.0;

ret *= normalize( ret );
ret = 1.0 - exp( ret * -3.0 ); //gamma





