s = 0.8;
l= 0.5;
//
//                            HSL to RGB by PieturP
cc=(6*h);
cc=if(below(cc,1),0,if(below(cc,2),1,if(below(cc,3),2,if(below(cc,4),3,if(below(cc,5),4,5)))));
