yp=(flip*0.4-0.2)*(sample);
zp=0;

//spin;
ang=tm*23 + sin(tm*76 + time*4)*0.3;;
sa=sin(ang);ca=cos(ang);
xr=xp*sa + yp*ca;
yr=xp*ca - yp*sa;
zr=zp;

xp=xr;
yp=yr + 0.05 + (sin(tm)*0.5 + 0.5)*0.2 + 0.05;
zp=zr;

//wrist movement;
