ang=(cos(tm*1)+cos(tm*0.653))*0.875 - 1.05;
sa=sin(ang);ca=cos(ang);
xq=xp*sa + zp*ca;
yq=yp;
zq=xp*ca - zp*sa;

//upper arm up down;
ang=0.5*(cos(tm*1.273)+cos(tm));
xp=xq;
sa=sin(ang);ca=cos(ang);
yp=yq*ca - zq*sa;
zp=yq*sa + zq*ca;

//xp=xq;yp=yq;zp=zq;

//project into screenspace and draw on screen
zp=zp+1.5;
xs=xp/zp;
ys=yp/zp;

x=xs+0.5;
y=ys*1.3+0.5;


a=(1-sample);

