frame_eqs_eel:
dec_m = pow(.9, 30/fps);dec_s = pow(.95, 30/fps);dec_xs = pow(.985, 30/fps);

flen = 128; shapes = 16;

beat = max(max(bass,treb),mid);
beat_ = beat_*dec_m + (1-dec_m)*beat; 
is_beat = above(beat, peak*6) * above (time, t0+.2);
t0 = is_beat*time + (1-is_beat)*t0;
peak = is_beat * beat + (1-is_beat)*peak*dec_m;
index = (index + is_beat) %32;
index2 = if (is_beat && (index%7==0), index2+1, index2)%64;
q22 = max(peak,.02);

ran1 = if (is_beat, rand(100)/25, ran1);
ran2 = if (is_beat, rand(100)/25, ran2);
q27 = ran1;
q28 = ran2;

n = 0; vol = .1; bbase = 5000;
loop (flen,
  value = gmegabuf(n) * (1+2/(n+.5));
  megabuf(bbase+n) = (beat >= (1.1-n/flen/2)*beat_)*(time >= megabuf(bbase+1000+n)+.1);
  trig = megabuf(bbase+n); ;
  megabuf(bbase+1000+n) = if(trig,time, megabuf(bbase+1000+n));
  dec = if(trig,0.2,.9-.1*n/flen);
  dec = pow(dec,30/fps);
  megabuf(n) = megabuf(n)*dec + (1-dec)*value;
  vol = vol + pow(megabuf(n),2);
  n = n+1;
);
vol = sqrt(vol);
vol_ = dec_m*vol_ + (1-dec_m)*vol;

// post smoothing
n = 0;
loop (flen,
  value = megabuf(n);
  dec = if (megabuf(1000+n)<=value,.7,.8-0.1*n/flen);
  dec = pow(dec,30/fps);
//  dec = 1; //#
    megabuf(n+1000) = megabuf(n+1000)*dec+ (1-dec)*value/vol_;
  n = n+1;
);


// Berechnung Ortskoordinaten aus Spektrum
dist = 2; reduce = flen/shapes;
n = 2000; m = 1000+dist; k=0;
loop (shapes,
  betr = (megabuf(m)+megabuf(m-dist)+megabuf(m+dist))/3;
  megabuf(n)   = megabuf(m) - megabuf(m+dist);
  megabuf(n+1) = megabuf(m) - megabuf(m-dist);
  z1 = max(.03/betr,.05);
