warp_hlsl:
shader_body
{
    float t = saturate((bass/bass_att-0.9)*13);
    uv = lerp(uv_orig, uv, t*0.5);

    float2 v1 = normalize(uv_orig-0.5);
    float2 v2 = v1.yx * float2(1,-1);

    float z = length(texsize.zw)*450;

    uv.xy += v1*texsize.zw * cos(rad*30 - time*7)*5;
    uv.xy += v2*texsize.zw * cos(ang*30 - time*7)*5;

    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;



    // darken (decay) over time
    ret = (ret - 0.04)*0.7;
}

