init_eqs_eel:
mv_x=64;mv_y=48;
nut=0;
stp=0;stq=0;
rtp=0;rtq=0;
wvr=0;
decay=0;
dcsp=0;
warp=0;
rotx=0;
roty=-13;

frame_eqs_eel:
musictime=musictime+(mid*mid*mid)*0.01;

xpos=sin(musictime*0.6)*0.3;
ypos=sin(musictime*0.4)*0.3;
q4=xpos;
q5=ypos;


//krash's beat detection code
volume = 0.3*(bass+mid+att);
beatrate = equal(beatrate,0) + (1-equal(beatrate,0))*(below(volume,0.01) + (1-below(volume,0.01))*beatrate);
lastbeat = lastbeat + equal(lastbeat,0)*time;
meanbass_att = 0.1*(meanbass_att*9 + bass_att);
peakbass_att = max(bass_att,peakbass_att);
beatrate = max(if(beat,if(below(time-lastbeat,2*beatrate),0.1*(beatrate*9 + time - lastbeat),beatrate),beatrate),0.1);
peakbass_att = beat*bass_att + (1-beat)*peakbass_att*(above(time - lastbeat, 2*beatrate)*0.95 + (1-above(time - lastbeat, 2*beatrate))*0.995);
lastbeat = beat*time + (1-beat)*lastbeat;
peakbass_att = max(peakbass_att,1.1*meanbass_att);
beat = above(volume,0.8)*below(peakbass_att - bass_att, 0.05*peakbass_att)*above(time - lastbeat, 0.1 + 0.5*(beatrate - 0.1));
beatcounter = beatcounter + beat;
mode = if(beat*equal(beatcounter%2,0),1-mode,mode);
flip = 2*mode-1;
monitor=flip;
q8=flip;


decay=0.98;
zoom=1.006;

vol=(bass+mid+treb)*0.25;
vol=vol*vol;
mtime=mtime+vol*0.3;//*flip;

q1=mtime*0.1;

warp=0.0;

fpsadjust = 85/fps;

stepp = 1;
stepm = fpsadjust * 0.03;

accellt = if( ctrllt, accellt + stepp, accellt - stepm );
accellt = min( max( accellt, 0 ) , 1 );

accelrt = if( ctrlrt, accelrt + stepp, accelrt - stepm );
accelrt = min( max( accelrt, 0 ) , 1 );

acceldn = if( ctrldn, acceldn + stepp, acceldn - stepm );
acceldn = min( max( acceldn, 0 ) , 1 );

accelup = if( ctrlup, accelup + stepp, accelup - stepm );
accelup = min( max( accelup, 0 ) , 1 );


rotx = rotx + accellt - accelrt;
roty = roty + accelup - acceldn;

q6 = rotx * 0.004;
q7 = roty * 0.004;
q3 = (accellt+acceldn+accelrt+accelup)/1.3 + 0.35;

monitor = q3;//stepp;//accellt;
