warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;
    float3 ret2 = tex2D( sampler_fc_main, uv ).xyz;

    ret.x = tex2D( sampler_main, (uv-0.5)*1.00+0.5 ).x;
    ret.y = tex2D( sampler_main, (uv-0.5)*0.98+0.5 ).y;
    ret.z = tex2D( sampler_main, (uv-0.5)*0.96+0.5 ).z;   

    // darken (decay) over time
    ret = ret- (ret*.5);
    ret = ret*(abs(GetBlur2(uv)-.5))*5;
    ret = ret - .01;
}

comp_hlsl:
shader_body
{

//Code bits by Flexi and Geiss

    float2 d = texsize.zw*8;
    float1 dx = ( GetBlur1(uv+float2(1,0)*d)-GetBlur1(uv-float2(1,0)*d) ).y;
    float1 dy = ( GetBlur1(uv+float2(0,1)*d)-GetBlur1(uv-float2(0,1)*d) ).y;
    float2 uv_y = uv+float2(dx,dy)*0.4;  
    float2 uv_y2 = (uv_y*1.25)-.125;  


    ret = lerp(ret,1.1-ret,.8*GetBlur1(uv_y)+.2*GetPixel(uv));
    ret = lerp(ret,ret-.135*GetBlur3(uv_y)+.125*GetBlur1(uv_y2)+.35*GetPixel(uv_y),.25);
    //ret *= .75;
    ret *= saturate(length(ret.xy-0.5)*5);
    ret = ret.xxy;
    ret += 1.15;
    float3 mask = GetBlur3(uv)-GetBlur1(uv)-0*GetPixel(uv_y);
    ret *= lerp(ret,ret*mask,pow(hue_shader.zxy,ret));
    ret = pow(ret,2.55);
}
