frame_eqs_eel:
warp = 0;

tic = min(time-tin,.1);
tin = time;

vol = (bass_att + treb_att + mid_att)*.333333;

ra = 1/tic*.1;

treb_avg = tic*(treb_avg*(1/tic - ra) + ra*treb);
mid_avg = tic*(mid_avg*(1/tic - ra) + ra*mid);
bass_avg = tic*(bass_avg*(1/tic - ra) + ra*bass);
vav = tic*(vav*(1/tic - ra) + ra*(bass+treb+mid)*.33333);

tt = tt + tic*treb;
mt = mt + tic*mid;
bt = bt + tic*bass;
vt = vt + tic*vav;

sp = abs(vav - slide)*.1;
slide = if(above(slide,vav),slide-tic*sp,slide+tic*sp) + (1-toc)*vav*.2;
toc = 1;

q1 = 0;

q2 = bt*.5 + 5;
q3 = mt*.5 + 3;
q4 = tt*.5;
q5 = .1 + (treb_avg + mid_avg)*.2;

zoom = 1 - bass_avg*.2;

monitor = q5;

pixel_eqs_eel:
coy = (coy + below(y,oy))*above(q1,0);
cox = (cox + 1)*above(q1,0)*equal(coy,ocoy);

moy = max(coy,moy);
mox = max(cox,mox);

nu = 4;
pox = ((cox/nu) - int(cox/nu))*nu;
poy = ((coy/nu) - int(coy/nu))*nu;


mod = (1-2*above(x,.5))*(1-2*above(y,.5));

dx = if(equal(coy%2,0),-equal(pox,1) + equal(pox,2),equal(pox,0) - equal(pox,3))*.001;
dy = if(equal(coy%2,0),-equal(pox,1) + equal(pox,2),equal(pox,0) - equal(pox,3))*.001*mod;

dis = (x + (1-y));

mod = if(above(dis,1),dis - 1,1-dis);
mod = below(mod,.2) + (1-min(1,(mod-.2)*4))*above(mod,.2)*1;

zm = q5;
modx = if(above(dis,1),zm,-zm);
mody = if(above(dis,1),-zm,zm);


dx = dx*mod + (1-mod)*modx;
dy = dy*mod + (1-mod)*mody;

rot = .3*mod;


oy = y;
ocoy = coy;
q1 = q1 + 1;

