warp:
shader_body { 
 vec3 ret_1;
 vec4 tmpvar_2;
 tmpvar_2 = texture (sampler_main, uv);
 ret_1 = (tmpvar_2.xyz + ((tmpvar_2.xyz - 
   ((texture (sampler_blur1, uv).xyz * scale1) + bias1)
 ) * 0.3));
 ret_1 = (ret_1 * 0.9);
 ret_1 = (ret_1 + ((
   (texture (sampler_noise_lq, (((uv_orig * texsize.xy) * (texsize_noise_lq.zw * 0.4)) + rand_frame.xy)).xyz - 0.5)
  / 256.0) * 112.0));
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, vec3(dot (ret_1, vec3(0.32, 0.49, 0.29))), vec3(0.2, 0.2, 0.2));
