warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // take the difference between the crisp and blurred images,
    // then add it back into the image.  Creates spots and stripes over time.
    ret += (ret - GetBlur1(uv))*0.3;
    ret *= 0.9;

    // add noise:
    float2 dither_uv = uv_orig*texsize.xy*texsize_noise_lq.zw * 0.4 + rand_frame.xy;
    ret += (tex2D(sampler_noise_lq, dither_uv).xyz-0.5)/256.0 * 112;

    // desaturate over time, to keep the globs white
    ret = lerp(ret, lum(ret), 0.2);
}

comp_hlsl:
shader_body
{
    float lum = tex2D(sampler_main, uv).z;
    ret = lerp(float3(0.3,0.2,0.4), float3(1.0,0.8,0.2), lum.xxx);
    ret -= (0.5-abs(lum-0.5))*2;
    ret *= 1.333; // a little bit of overbright
}
