warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_fc_main, (-uv+0.5)*(1 - rad*0.05) + 0.5 ).xyz;

    // darken (decay) over time
    ret *= 1; //or try: ret -= 0.004;
}

