   (texture (sampler_blur1, uv).xyz * scale1)
  + bias1) * 0.4));
 vec3 tmpvar_2;
 tmpvar_2.x = (1.7 - (bass * 0.5));
 tmpvar_2.y = (1.7 - (treb * 0.5));
 tmpvar_2.z = (1.7 - (mid * 0.5));
 vec3 tmpvar_3;
 tmpvar_3 = pow (pow (ret_1, vec3(0.5, 0.8, 1.7)), tmpvar_2);
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

