warp_hlsl:
shader_body
{

float2 zoom = 2.9;
float2 cntr = float2(-0,-0);
float2 c = float2(0,2); // complex constant

 float2 my_uv = (uv-0.5-cntr)*zoom; // center and scale dx/dy pane
 float2 u2 = float2(my_uv.x*my_uv.x - my_uv.y*my_uv.y, 2*my_uv.x*my_uv.y); //complex squaring of u
 float2 cu2 = float2(u2.x*c.x - u2.y*c.y, u2.x*c.y + c.x*u2.y); // complex multiplication by c
 float denom = 1/((cu2.x+1)*(cu2.x+1)+cu2.y*cu2.y);
 my_uv = float2(u2.x*(cu2.x+1)+u2.y*cu2.y,u2.y*(cu2.x+1)-u2.x*cu2.y)*denom; // complex division u^2/(1+c*u^2)

zoom = 1.84+(bass-treb)*0.0;
cntr = float2(0,0);
c = float2(0.2806,0.4508);

 float2 my_uv2 = (uv_orig-0.5-cntr)*zoom; // using uv_orig here - no contortion to the nice julia island
 my_uv2 = float2(my_uv2.x*my_uv2.x - my_uv2.y*my_uv2.y, 2*my_uv2.x*my_uv2.y) + c; // u -> u^2 + c

ret.z = tex2D( sampler_fw_main, my_uv - floor(my_uv) ).z*0.9- 0.04;
ret.x = tex2D( sampler_fc_main, my_uv2).x - 0.008;

 my_uv = lerp(my_uv,my_uv2,0.8); // linear interpolation between the two solutions

ret.y = tex2D( sampler_fw_main, my_uv - floor(my_uv) ).y*0.9- 0.004;

}
