dx=dx-0.05*sin(time*19.63+.5+(rad*2-1)*(sin(time*1.26) *6 ));
dy=dy-0.01205*sin((ang*2-1)*int(sin(time/27) *3+6));
zoom=800+sin(time/2);
///////////////////////////////////////
cr1=(int(sin(time*30)/2+.5+.1));
cr2=cr1;
crx=cr2-cr1;
ci=rad;
cb=.25  ;
crs=2;
cr=pow(sin(ci*6.3-(cb*6.3))/2+.5,crs)*crx+cr1;
cqr=if( above (ci,.25+cb),cr,crx+cr1);crr=if( below(ci,1.5),cqr,crx+cr1);
