shader_body { 
 vec4 tmpvar_1;
 tmpvar_1.w = 1.0;
 float tmpvar_2;
 tmpvar_2 = dot (texsize.zw, texsize.zw);
 vec2 tmpvar_3;
 tmpvar_3 = (uv - 0.5);
 tmpvar_1.xyz = (max (max (texture (sampler_main, uv).xyz, texture (sampler_main, 
   ((tmpvar_3 * (1.0 - (8.0 * 
     sqrt(tmpvar_2)
   ))) + 0.5)
 ).xyz), texture (sampler_main, (
   (tmpvar_3 * (1.0 + (8.0 * sqrt(tmpvar_2))))
  + 0.5)).xyz) - 0.034);
 ret = tmpvar_1.xyz;
}

comp:
shader_body { 
 vec3 ret_1;
 vec2 tmpvar_2;
 tmpvar_2 = ((vec2(-1.01, 1.01) * (uv - 0.5)) + 0.5);
 ret_1 = ((texture (sampler_main, uv).xyz * 0.4) + ((
   (texture (sampler_blur2, uv).xyz * scale2)
  + bias2) * 1.75));
 vec3 tmpvar_3;
 tmpvar_3 = mix (ret_1, ((texture (sampler_main, tmpvar_2).xyz * 0.4) + (
   ((texture (sampler_blur2, tmpvar_2).xyz * scale2) + bias2)
  * 1.75)), vec3(0.5, 0.5, 0.5));
 ret_1 = tmpvar_3;
 vec4 tmpvar_4;
 tmpvar_4.w = 1.0;
 tmpvar_4.xyz = tmpvar_3;
 ret = tmpvar_4.xyz;
}

warp_hlsl:
shader_body
{
    // sample previous frame
    ret = tex2D( sampler_main, uv ).xyz;

    // feather pen
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1-8*length(texsize.zw)) + 0.5 ).xyz);
    ret = max(ret, tex2D( sampler_main, (uv-0.5)*(1+8*length(texsize.zw)) + 0.5 ).xyz);

    // darken over time
    ret -= 0.034;
}

comp_hlsl:
shader_body
{


    float2 uv_echo = (uv - 0.5)*1.010*float2(-1,1) + 0.5;

    ret = GetPixel(uv)*0.4 + GetBlur2(uv)*1.75;
    float3 ret2 = GetPixel(uv_echo)*0.4 + GetBlur2(uv_echo)*1.75;

    ret = lerp(ret,ret2,.5);
}
